function InitViewport(){const minwidth=320;SetViewport(320),window.addEventListener("resize",(function(event){SetViewport(320)}))}function SetViewport(minwidth){let viewport=document.querySelector('meta[name="viewport"]');screen.width<minwidth?viewport.content="user-scalable=no,width="+minwidth:viewport.content="width=device-width,initial-scale=1"}document.addEventListener("DOMContentLoaded",(function(){InitViewport()}));